package com.guide.common.utli;

import cn.hutool.core.img.Img;
import cn.hutool.core.io.FileUtil;
import cn.hutool.core.io.IoUtil;

import javax.imageio.ImageIO;
import java.awt.*;
import java.awt.image.BufferedImage;
import java.io.ByteArrayInputStream;
import java.io.FileInputStream;
import java.io.IOException;
import java.io.InputStream;
import java.util.Base64;

public class ImageTest
{
    public static void main(String[] args) throws IOException
    {
        String base64 = "";
        Base64.Decoder decoder = Base64.getDecoder();
        ByteArrayInputStream stream = IoUtil.toStream(decoder.decode(base64));
        getInfo1(stream);
        getInfo2(stream);
    }

    public static void getInfo1(ByteArrayInputStream stream) throws IOException
    {
        BufferedImage bufferedImage = ImageIO.read(stream);
        System.out.println("Width:" + bufferedImage.getWidth());
        System.out.println("Height:" + bufferedImage.getHeight());
    }

    public static void getInfo2(ByteArrayInputStream stream) throws IOException
    {
        Img img = Img.from(stream);
        Image image = img.getImg();
        System.out.println("Width:" + image.getWidth(null));
        System.out.println("Height:" + image.getHeight(null));
    }
}
